package com.example.marvelapp.data.db;import android.content.Context;import androidx.annotation.NonNull;import androidx.room.Room;import org.jetbrains.annotations.NotNull;import javax.inject.Singleton;import dagger.Module;import dagger.Provides;import dagger.hilt.InstallIn;import dagger.hilt.android.qualifiers.ApplicationContext;import dagger.hilt.components.SingletonComponent;@Module@InstallIn({SingletonComponent.class})public class DatabaseModule {    @Provides    MarvelDatabase providesMarvelDatabase(@ApplicationContext Context context) {        return Room.databaseBuilder(             context,             MarvelDatabase.class,             "marvelDB"        ).build();    }    @Provides    CharacterDao provideChannelDao(@NonNull MarvelDatabase marvelDatabase) {        return marvelDatabase.characterDao();    }}