package com.example.marvelapp.data.model;import android.os.Parcel;import android.os.Parcelable;import androidx.room.ColumnInfo;import androidx.room.Embedded;import androidx.room.Entity;import androidx.room.PrimaryKey;import com.google.gson.annotations.SerializedName;@Entity(tableName = "character")public class Character implements Parcelable {    @PrimaryKey    @SerializedName("id")    private int id;    @ColumnInfo(name = "name")    @SerializedName("name")    private String name;    @ColumnInfo(name = "description")    @SerializedName("description")    private String description;    @Embedded    @SerializedName("thumbnail")    private CharacterImage thumbnail;    public Character() {    }    public Character(int id, String name, String description, CharacterImage thumbnail) {        this.id = id;        this.name = name;        this.description = description;        this.thumbnail = thumbnail;    }    protected Character(Parcel in) {        id = in.readInt();        name = in.readString();        description = in.readString();    }    public static final Creator<Character> CREATOR = new Creator<Character>() {        @Override        public Character createFromParcel(Parcel in) {            return new Character(in);        }        @Override        public Character[] newArray(int size) {            return new Character[size];        }    };    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public CharacterImage getThumbnail() {        return thumbnail;    }    public void setThumbnail(CharacterImage thumbnail) {        this.thumbnail = thumbnail;    }    @Override    public int describeContents() {        return 0;    }    @Override    public void writeToParcel(Parcel parcel, int i) {        parcel.writeInt(id);        parcel.writeString(name);        parcel.writeString(description);    }}