package com.example.marvelapp.data.model;public class CharacterPage {    private int limit;    private int offset;    public CharacterPage() {    }    public CharacterPage(int limit, int offset) {        this.limit = limit;        this.offset = offset;    }    public CharacterPage withLimitAndOffset(int limit, int offset) {        return new CharacterPage(limit, offset);    }    public int getLimit() {        return limit;    }    public int getOffset() {        return offset;    }}