package com.example.marvelapp.data.model;import android.os.Parcel;import android.os.Parcelable;import com.google.gson.annotations.SerializedName;public class Comics implements Parcelable {    @SerializedName("id")    private String id;    @SerializedName("title")    private String title;    @SerializedName("description")    private String description;    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public CharacterImage getThumbnail() {        return thumbnail;    }    public void setThumbnail(CharacterImage thumbnail) {        this.thumbnail = thumbnail;    }    @SerializedName("thumbnail")    private CharacterImage thumbnail;    protected Comics(Parcel in) {        id = in.readString();        title = in.readString();        description = in.readString();        thumbnail = in.readParcelable(CharacterImage.class.getClassLoader());    }    Comics(){    }    public static final Creator<Comics> CREATOR = new Creator<Comics>() {        @Override        public Comics createFromParcel(Parcel in) {            return new Comics(in);        }        @Override        public Comics[] newArray(int size) {            return new Comics[size];        }    };    @Override    public int describeContents() {        return 0;    }    @Override    public void writeToParcel(Parcel parcel, int i) {        parcel.writeString(id);        parcel.writeString(title);        parcel.writeString(description);        parcel.writeParcelable(thumbnail, i);    }}