package com.example.marvelapp.data.network;public class NetworkUtils {   public static String PUBLIC_KEY = "38a5485718da0c78ba7d27abae63b5d9";   public static String PRIVATE_KEY = "e43bbfddea167bdffc2981a7b859100fbc85abe1";    public static String md5(String input) {        try {            java.security.MessageDigest md = java.security.MessageDigest.getInstance("MD5");            byte[] array = md.digest(input.getBytes());            StringBuffer sb = new StringBuffer();            for (int i = 0; i < array.length; ++i) {                sb.append(Integer.toHexString((array[i] & 0xFF) | 0x100).substring(1, 3));            }            return sb.toString();        } catch (java.security.NoSuchAlgorithmException e) {        }        return null;    }}