package com.example.marvelapp.data.network.interceptor;import androidx.annotation.NonNull;import java.io.IOException;import okhttp3.Interceptor;import okhttp3.Request;import okhttp3.Response;class NetworkNotAvailableException extends IOException{    public NetworkNotAvailableException() {        super("Internet connection not available");    }}public class NetworkReachabilityInterceptor implements Interceptor {    NetworkStateChecker networkStateChecker;    public NetworkReachabilityInterceptor(NetworkStateChecker networkStateChecker){        this.networkStateChecker = networkStateChecker;    }    @NonNull    @Override    public Response intercept(@NonNull Chain chain) throws IOException {        if(!networkStateChecker.isNetworkAvailable()){            throw new NetworkNotAvailableException();        }        Request request = chain.request();        return chain.proceed(request);    }}