package com.example.marvelapp.data.network.interceptor;import android.content.Context;import android.net.ConnectivityManager;import android.net.Network;import android.net.NetworkCapabilities;import javax.inject.Inject;import dagger.hilt.android.qualifiers.ApplicationContext;public class NetworkStateCheckerImpl implements NetworkStateChecker {    private final Context context;    @Inject    NetworkStateCheckerImpl(@ApplicationContext Context context) {        this.context = context;    }    @Override    public boolean isNetworkAvailable() {        ConnectivityManager connectivityManager =                (ConnectivityManager) context.getSystemService(Context.CONNECTIVITY_SERVICE);        Network network = connectivityManager.getActiveNetwork();        NetworkCapabilities activeNetwork = connectivityManager.getNetworkCapabilities(network);        return activeNetwork.hasTransport(NetworkCapabilities.TRANSPORT_WIFI)                || activeNetwork.hasTransport(NetworkCapabilities.TRANSPORT_CELLULAR);    }}