package com.example.marvelapp.data.network.service;import com.example.marvelapp.data.model.Character;import com.example.marvelapp.data.model.CharacterDataWrapper;import com.example.marvelapp.data.model.ComicDataWrapper;import com.example.marvelapp.data.model.Comics;import java.util.List;import io.reactivex.rxjava3.core.Observable;import io.reactivex.rxjava3.core.Single;import retrofit2.http.GET;import retrofit2.http.Path;import retrofit2.http.Query;public interface MarvelApiService {    @GET("/v1/public/characters")    Observable<CharacterDataWrapper> loadCharacters(@Query("limit") int limit, @Query("offset") int offset);    @GET("/v1/public/characters/{characterId}/comics")    Observable<ComicDataWrapper> loadComics(@Path("characterId") int characterId);}